import getNumber from"./get-number";import testComputedSize from"./computed-size";export default function getNumberStyle(element,style,styles=window.getComputedStyle(element,null)){let value=getNumber(styles[style]);if(!testComputedSize()&&style==="width"){value+=getNumber(styles.paddingLeft)+getNumber(styles.paddingRight)+getNumber(styles.borderLeftWidth)+getNumber(styles.borderRightWidth)}else if(!testComputedSize()&&style==="height"){value+=getNumber(styles.paddingTop)+getNumber(styles.paddingBottom)+getNumber(styles.borderTopWidth)+getNumber(styles.borderBottomWidth)}return value}