import Point from"./point";import Classes from"./classes";let id=0;class ShuffleItem{constructor(element,isRTL){id+=1;this.id=id;this.element=element;this.isRTL=isRTL;this.isVisible=true;this.isHidden=false}show(){this.isVisible=true;this.element.classList.remove(Classes.HIDDEN);this.element.classList.add(Classes.VISIBLE);this.element.removeAttribute("aria-hidden")}hide(){this.isVisible=false;this.element.classList.remove(Classes.VISIBLE);this.element.classList.add(Classes.HIDDEN);this.element.setAttribute("aria-hidden",true)}init(){this.addClasses([Classes.SHUFFLE_ITEM,Classes.VISIBLE]);this.applyCss(ShuffleItem.Css.INITIAL);this.applyCss(this.isRTL?ShuffleItem.Css.DIRECTION.rtl:ShuffleItem.Css.DIRECTION.ltr);this.scale=ShuffleItem.Scale.VISIBLE;this.point=new Point}addClasses(classes){classes.forEach((className=>{this.element.classList.add(className)}))}removeClasses(classes){classes.forEach((className=>{this.element.classList.remove(className)}))}applyCss(obj){Object.keys(obj).forEach((key=>{this.element.style[key]=obj[key]}))}dispose(){this.removeClasses([Classes.HIDDEN,Classes.VISIBLE,Classes.SHUFFLE_ITEM]);this.element.removeAttribute("style");this.element=null}}ShuffleItem.Css={INITIAL:{position:"absolute",top:0,visibility:"visible",willChange:"transform"},DIRECTION:{ltr:{left:0},rtl:{right:0}},VISIBLE:{before:{opacity:1,visibility:"visible"},after:{transitionDelay:""}},HIDDEN:{before:{opacity:0},after:{visibility:"hidden",transitionDelay:""}}};ShuffleItem.Scale={VISIBLE:1,HIDDEN:.001};export default ShuffleItem;