function randomize(array){let n=array.length;while(n){n-=1;const i=Math.floor(Math.random()*(n+1));const temp=array[i];array[i]=array[n];array[n]=temp}return array}const defaults={reverse:false,by:null,compare:null,randomize:false,key:"element"};export default function sorter(arr,options){const opts={...defaults,...options};const original=Array.from(arr);let revert=false;if(!arr.length){return[]}if(opts.randomize){return randomize(arr)}if(typeof opts.by==="function"){arr.sort(((a,b)=>{if(revert){return 0}const valA=opts.by(a[opts.key]);const valB=opts.by(b[opts.key]);if(valA===undefined&&valB===undefined){revert=true;return 0}if(valA<valB||valA==="sortFirst"||valB==="sortLast"){return-1}if(valA>valB||valA==="sortLast"||valB==="sortFirst"){return 1}return 0}))}else if(typeof opts.compare==="function"){arr.sort(opts.compare)}if(revert){return original}if(opts.reverse){arr.reverse()}return arr}